INSERT INTO `er_posts` VALUES("894", "1", "2018-05-28 00:19:38", "2018-05-27 17:19:38", "Những cô nàng kẹo ngọt là nguồn cảm hứng mà thời trang Elise đưa vào bộ sưu tập mới nhất của mình. Các thiết kế chủ yếu sử dụng chất liệu mềm mát của mùa hè và màu sắc thì vô cùng rực rỡ, bắt mắt bằng hoạ tiết in nhưng được cân bằng, tinh tế trên nền đen trắng truyền thống. Fruit Candy với tinh thần tươi trẻ nhưng không kém phần quyến rũ dành cho những cô nàng kẹo ngọt đắm mình vào khu vườn thiên nhiên cổ tích.\r\n<div class=\"text-center\"><img class=\"img-responsive\" src=\"http://elise.vn/images/collection/2018/04/original/_MG_7250%20copy.jpg\" /></div>", "FRUIT CANDY", "", "publish", "open", "open", "", "fruit-candy", "", "", "2018-05-28 00:19:38", "2018-05-27 17:19:38", "", "0", "http://thoitrangthietke.thainguyenweb.com/?p=894", "0", "post", "", "0");
