INSERT INTO `er_posts` VALUES("897", "1", "2018-05-28 00:20:06", "2018-05-27 17:20:06", "Fruit Candy lấy cảm hứng từ sự tươi trẻ, ngọt ngào càng trở nên đáng yêu hơn bao giờ hết với phiên bản dành cả cho mẹ và bé. Còn gì tuyệt vời hơn khi cùng bé yêu đắm chìm trong khu vườn cổ tích ngập tràn sắc màu thời trang.", "FRUIT CANDY PHIÊN BẢN MẸ VÀ BÉ", "", "publish", "open", "open", "", "fruit-candy-phien-ban-me-va-be", "", "", "2018-05-28 00:20:06", "2018-05-27 17:20:06", "", "0", "http://thoitrangthietke.thainguyenweb.com/?p=897", "0", "post", "", "0");
